import Vue from "vue"
import VueRouter from "vue-router"
Vue.use(VueRouter);


const routes = [
    {

        path: "/login",
        component: () => import("@/view/Login.vue")
    },
  
    {
        path:"/",
        redirect:"/login"
    },
    {
        path:"/center",
        name:"center",
        component:() => import("@/view/Center.vue")
    },
    
]

const router = new VueRouter({
    routes,
})

router.beforeEach((to,from,next)=>{
    //读取所有cookie
